function postgres_connstring (opts) {
  var opts = opts || {}

  if (!opts.config) {
    console.error ('Missing config')
    return null
  }

  if (!opts.config.db) {
    console.error ('Missing db from config')
    return null;
  }

  var dbConfig = opts.config.db
  return 'tcp://' + dbConfig.user + ':' + dbConfig.password + '@' + dbConfig.host + '/' + dbConfig.database

}

module.exports = postgres_connstring;