var test = require('tap').test
var connstring = require('../db_connstring.js')

test('Make sure error callback on no config', function (t) {
  t.equal (null, connstring(null));
  t.end()
})

test('Make sure error callback on no db in config', function (t) {
  t.equal (null, connstring({
    config: {}
  }))
  t.end()
})

